import React from 'react';
import './Movie.css';

const Movie = ({ movie, selected, onClick, plot }) => {
  return (
    <div className="movie-container">
      <img className="movie-image" src={movie.Poster} alt={movie.Title} />
      <div className="movie-details">
        <h3 className="movie-title">{movie.Title}</h3>
        <p className="movie-info">Year of Release: {movie.Dtse || 'N/A'}</p>
        <p className="movie-info">Type: {movie.Type}</p>
        {selected && <p className="movie-plot">{plot}</p>}
        <button className="movie-button" onClick={onClick}>
          {selected ? 'Hide Plot' : 'Show Plot'}
        </button>
      </div>
    </div>
  );
};

export default Movie;
