// StarRating.js

import React from 'react';
import './StarRating.css';

const StarRating = ({ rating }) => {
  const maxRating = 10;
  const fullStars = Math.floor(rating);
  const remainder = rating % 1;
  const starPercentage = (fullStars / maxRating) * 100;
  const remainderPercentage = (remainder * 100).toFixed(2);

  return (
    <div className="star-rating">
      <div className="stars-inner" style={{ width: `${starPercentage}%` }}>
        <span>&#9733;&#9733;&#9733;&#9733;&#9733;</span>
        <span className="partial-star" style={{ width: `${remainderPercentage}%` }}>&#9733;</span>
      </div>
      <div className="stars-outer">
        <span>&#9733;&#9733;&#9733;&#9733;&#9733;</span>
      </div>
    </div>
  );
};

export default StarRating;
