import React from 'react';

const Movie = ({ movie, selected, onClick }) => {
  return (
    <div>
      <img src={movie.Poster} alt={movie.Title} />
      <div>
        <h3>{movie.Title}</h3>
        <p>Release Date: {movie.Year}</p>
        <p>Type: {movie.Type}</p>
        <button onClick={onClick}>{selected ? 'Hide Plot' : 'Show Plot'}</button>
        {selected && <p>{movie.Plot}</p>}
      </div>
    </div>
  );
};

export default Movie;
